* {
    font-family: "SpaceMono Nerd Font";
    font-size: 16px;
    font-weight: 400;
}

#window {
    margin: 0px;
    border: 2px solid #ffe600; /* Cyberpunk yellow */
    border-radius: 10px;
    background-color: rgba(0, 0, 0, 0.7);
    box-shadow: 0 0 10px #ffe600;
}

#input {
    margin: 25px;
    background-color: rgba(0, 0, 0, 0.5);
    color: #ffe600;
    border-radius: 10px;
    border: 2px solid #ffe600;
    padding: 6px;
    box-shadow: 0 0 8px #ffe600;
    caret-color: #ff003c; /* Cyberpunk red */
}

#scroll {
    margin-bottom: 25px;
}

#entry {
    margin: 0px 25px;
    color: #ffe600;
    background-color: transparent;
    border-radius: 8px;
    padding: 4px;
    transition: background 0.2s ease;
}

#entry:selected {
    background-color: #ff003c; /* Cyberpunk red */
    border-radius: 10px;
    color: #0d0d0d; /* Dark text on red */
    box-shadow: 0 0 10px #ff003c;
}

#entry > box {
    margin-left: 15px;
}

#entry image {
    padding-right: 10px;
    opacity: 0.85;
}

#expander-box {
    background-color: transparent;
}

